
public class Ex8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double pi = 3.14159;
		double radius = 5.5;
		double perimeter = 2 * radius * pi;
		double area = 2 * radius * radius;
		
		System.out.println("The area of a circle with radius 5.5 is " + area + " square units.");
		System.out.println("The perimeter of a circle with radius 5.5 is " + perimeter + " units.");
	}

}
